import React, { useState, useEffect } from 'react';

function OffersBox() {
  const [backgroundImage, setBackgroundImage] = useState('image1.jpg'); // Initial background image state

  // Array of background images
  const img1="data:image/jpeg;base64,"
  const img2 ="data:image/jpeg;base64,"
  const img3 = "data:image/jpeg;base64,"
  const img4 = "data:image/jpeg;base64,"
const img5 = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSGV_NaItEOWUaKdAUdMbtsdMT5IzzcqmTCKQ&usqp=CAU"
  const backgroundImages = [img1, img2, img3, img5, img4];

  // Function to change background image
  useEffect(() => {
    const interval = setInterval(() => {
      // Get random index for background image array
      const randomIndex = Math.floor(Math.random() * backgroundImages.length);
      setBackgroundImage(backgroundImages[randomIndex]);
    }, 5000); // Change image every 5 seconds

    return () => clearInterval(interval); // Cleanup function to clear interval
  }, [backgroundImages]);

  return (
    <div className="offers-box" style={{ backgroundImage: `url(${backgroundImage})` }}>
      
    </div>
  );
}

export default OffersBox;
